        public List<NoteableMenuItem> CustomPresets { get; set; } = new();

        public NoteablePreset()
        {
            if (ODPresetGroups.Count > 0)
            {
                return;
            }

            ODPresetGroup bioGroup = new()
            {
                GroupName = "Odyssey Biologicals"
            };

            bioGroup.MenuItems.Add(DeseriliseString(aleoida));
            bioGroup.MenuItems.Add(DeseriliseString(bacterium));
            bioGroup.MenuItems.Add(DeseriliseString(cactoida));
            bioGroup.MenuItems.Add(DeseriliseString(clypeus));
            bioGroup.MenuItems.Add(DeseriliseString(concha));
            bioGroup.MenuItems.Add(DeseriliseString(electricae));
            bioGroup.MenuItems.Add(DeseriliseString(fonticulua));
            bioGroup.MenuItems.Add(DeseriliseString(frutexa));
            bioGroup.MenuItems.Add(DeseriliseString(fumerola));
            bioGroup.MenuItems.Add(DeseriliseString(fungoida));
            bioGroup.MenuItems.Add(DeseriliseString(osseus));
            bioGroup.MenuItems.Add(DeseriliseString(recepta));
            bioGroup.MenuItems.Add(DeseriliseString(stratum));
            bioGroup.MenuItems.Add(DeseriliseString(tubus));
            bioGroup.MenuItems.Add(DeseriliseString(tussock));

            ODPresetGroups.Add(bioGroup);
        }

        internal void AddCustom(NoteableMenuItem menuItem)
        {
            CustomPresets.Add(menuItem);
            AddCustomMenuItem(menuItem);
        }

        private void AddCustomMenuItem(NoteableMenuItem menuItem)
        {
            MenuItem CustomItem = MenuItems.FirstOrDefault(x => (string)x.Header == "Custom Presets");

            if (CustomItem == default)
            {
                CustomItem = new()
                {
                    Header = "Custom Presets"
                };

                MenuItems.Add(CustomItem);
            }

            MenuItem menuItem1 = new()
            {
                Header = menuItem.Header,
                Tag = menuItem,
                ToolTip = "Right click to delete"
            };

            menuItem1.Click += MenuItem1_Click;
            menuItem1.PreviewMouseRightButtonUp += MenuItem1_PreviewMouseRightButtonUp;
            CustomItem.Items.Add(menuItem1);
        }

        private void MenuItem1_PreviewMouseRightButtonUp(object sender, System.Windows.Input.MouseButtonEventArgs e)
        {
            MenuItem menuItem = sender as MenuItem;

            NoteableMenuItem noteableMenu = menuItem.Tag as NoteableMenuItem;

            MessageBoxResult msg = ODMessageBox.Show(Application.Current.Windows.OfType<DisplaySettingsView>().First(), $"Delete {noteableMenu.Header} Preset?", MessageBoxButton.YesNo); ;

            if(msg == MessageBoxResult.Yes)
            {
                MenuItem CustomItem = MenuItems.FirstOrDefault(x => (string)x.Header == "Custom Presets");

                if (CustomItem == default)
                {
                    return;
                }

                CustomItem.Items.Remove(menuItem);
                CustomPresets.Remove(noteableMenu);

                if(CustomItem.Items.Count == 0)
                {
                    MenuItems.Remove(CustomItem);
                }
            }
        }

        private static object DeseriliseString(string v)
        {
            return JsonConvert.DeserializeObject(v);
        }

        public void BuildMenu()
        {
            foreach (ODPresetGroup group in ODPresetGroups)
            {
                MenuItem groupItem = new()
                {
                    Header = group.GroupName
                };

                foreach (object item in group.MenuItems)
                {
                    if (((JObject)item)["MenuItems"] != null)
                    {
                        ODPresetSubMenuItem item1 = ((JObject)item).ToObject<ODPresetSubMenuItem>();

                        MenuItem subMenuItem = new()
                        {
                            Header = item1.MenuTitle
                        };

                        foreach (object menuItem in item1.MenuItems)
                        {
                            NoteableMenuItem item2 = ((JObject)menuItem).ToObject<NoteableMenuItem>();

                            MenuItem menuItem1 = new()
                            {
                                Header = item2.Header,
                                Tag = item2
                            };

                            menuItem1.Click += MenuItem1_Click;
                            subMenuItem.Items.Add(menuItem1);
                        }
                        groupItem.Items.Add(subMenuItem);
                        continue;
                    }
                }

                MenuItems.Add(groupItem);
            }

            if(CustomPresets.Count == 0)
            {
                return;
            }

            foreach (NoteableMenuItem item in CustomPresets)
            {
                AddCustomMenuItem(item);
            }
        }

        public event EventHandler<NoteableMenuItemClickArgs> OnMenuItemClick;

        private void MenuItem1_Click(object sender, RoutedEventArgs e)
        {
            var menu = (MenuItem)sender;

            var menuitem = menu.Tag as NoteableMenuItem;

            OnMenuItemClick?.Invoke(menu, new NoteableMenuItemClickArgs(menuitem));
        }
    }
